import * as THREE from "three";
import React, { useRef } from "react";
import { Canvas } from "@react-three/fiber";

type Props = {
  surface: number[];
  cubeColor?: string;
};

const Box = ({ x, y, z, cubeColor = "#ccc" }: { x: number; y: number; z: number; cubeColor?: string }) => {
  const meshRef = useRef<THREE.Mesh>(null);

  return (
    <mesh
      ref={meshRef}
      position={[x, y, z]}
    >
      <boxBufferGeometry args={[1, 1, 1]} />
      <meshStandardMaterial color={cubeColor} />
    </mesh>
  );
};

const Surface = ({ surface, cubeColor }: Props) => {
  const cubes: JSX.Element[] = [];

  let x = 0;
  let z = 0;

  surface.forEach((y, i) => {
    for (let j = 0; j < y; j++) {
      cubes.push(<Box key={`${i}-${j}`} x={x} y={y / 2} z={z} cubeColor={cubeColor} />);
      z += 1;
    }

    x += 1;
    z = 0;
  });

  return <>{cubes}</>;
};

const Scene = ({ surface, cubeColor = "#ccc" }: Props) => {
  return (
    <Canvas style={{ width: "100%", height: "100%" }}>
      <ambientLight intensity={0.2} />
      <pointLight position={[10, 10, 10]} />
      <Surface surface={surface} cubeColor={cubeColor} />
    </Canvas>
  );
};

export default Scene;
