import * as THREE from "three";
import React, { useRef, useState } from "react";
import { Canvas, useFrame, useThree } from "@react-three/fiber";


// TYPY
type Props = {
  surface: number[];
};

const boxArgs = [2, 1, 1];
const edgesGeometry = new THREE.EdgesGeometry(new THREE.BoxGeometry(...boxArgs));

const Box = ({ x, y, z }: { x: number; y: number; z: number }) => {
  return (
    <mesh position={[x, y, z]}>
      <boxBufferGeometry args={boxArgs} />
      <meshStandardMaterial color="gray" />
      <lineSegments geometry={edgesGeometry}>
        <lineBasicMaterial color="white" linewidth={2} />
      </lineSegments>
    </mesh>
  );
};

const Surface = ({ surface }: Props) => {
  const cubes: JSX.Element[] = [];
  let x = 0;
  let y = 0;
  let z = 0;
  const surfaceLength = surface.length;

  //SKALOWANIE X [-polowa/mask do polowa/maks] na osi
  const rowWidth = surfaceLength * boxArgs[0]; // Szerokość wiersza
  let rowStartX = -rowWidth + boxArgs[0]; // Początkowa pozycja X wiersza
  rowStartX = rowStartX / 2;


  //SKALOWANIE Y [maksymalna wysokość] na osi
  const maxSurfaceValue = Math.max(...surface);
  let columnHeight = -maxSurfaceValue * boxArgs[1];
  columnHeight = columnHeight / 2;

  const exportRowStartX = rowStartX;
  const exportColumnHeight = columnHeight;

  surface.forEach((surrfaceIndex, i) => {
    
    console.log("rowWidth")
    console.log(rowWidth)
    console.log("boxarguments")
    console.log(boxArgs[0])
    console.log(boxArgs)
    console.log("rowStartX")
    console.log(rowStartX)
    console.log("height")
    console.log(surrfaceIndex)
    console.log("i")
    console.log(i)
    
    for (let j = 0; j < surrfaceIndex; j++) {
      const posX = rowStartX;
      const posY = columnHeight + j * boxArgs[1]; // Obliczenie pozycji Y kostki
      cubes.push(<Box key={`${i}-${j}`} x={posX} y={posY} z={z} />);
      console.log(rowStartX)
      console.log(posX, posY, z);
    }
    
    rowStartX += boxArgs[0];
    console.log("INKREMENTACJA")
    console.log(rowStartX)
  });

  return {
    exportRowStartX,
    exportColumnHeight,
    elements: (
      <>
        {cubes}
      </>
    ),
  };
};


const Axes = () => {
  const axesHelperRef = useRef<THREE.AxesHelper>(null);

  useFrame((state) => {
    if (axesHelperRef.current) {
      axesHelperRef.current.scale.set(50, 50, 50);
    }
  });

  return <axesHelper ref={axesHelperRef} args={[5]} />;
};

const Scene = ({ surface }: Props) => {
  const { elements, exportRowStartX, exportColumnHeight } = Surface({ surface });

  // Obliczenie odległości kamery od planszy
  const { viewport } = useThree();
  const cameraDistance = Math.max(
    surface.length * boxArgs[0] / 2 / viewport.width,
    Math.max(...surface) * boxArgs[1] / 2 / viewport.height,
  ) * 1.5; // Mnożnik, aby dać trochę luzu

  return (
    <Canvas style={{ background: "pink" }}>
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <group position={[exportRowStartX, exportColumnHeight, -cameraDistance]}>
        {elements}
      </group>
      <Axes />
    </Canvas>
  );
};
export default Scene;
