import React, { useState, useRef } from 'react';
import lottie from 'lottie-web';
import animationData from './animation/cloud.json';
import { Stage, Layer, Rect, Group } from "react-konva";
import whitecloud from './assets/whitecloud.png';
import { getWaterVolume } from './api/controller';
import findWaterBlocks from './utils/findWaterBlocks';
import './App.css';
import Scene from "./components/scene";

function App() {
  const inputRef = useRef<HTMLInputElement>(null);
  const [surfaceArray, setSurfaceArray] = useState<number[]>([]);
  const [waterBlocks, setWaterBlocksArray] = useState<number[][]>([]);
  const [key, setKey] = useState(0);
  const [showScene, setShowScene] = useState(true);

  const handleClick = async () => {
    setKey(key + 1); // zmiana klucza, aby wymusić ponowne wyrenderowanie
    setShowScene(true); // ustawienie flagi, aby wyświetlić "Scene"
    try {
      const inputValue = inputRef.current?.value;
      if (!inputValue) {
        console.log('Pole tekstowe jest puste');
        return;
      }
      const surfaceArray = inputValue.split(',').map(Number);
      console.log(surfaceArray);
      setSurfaceArray(surfaceArray);
      const waterBlocks = findWaterBlocks(surfaceArray);
      const volume = await getWaterVolume(surfaceArray);
      console.log(volume)
      setKey(key + 1); // zmiana klucza, aby wymusić ponowne wyrenderowanie
      setShowScene(true); // ustawienie flagi, aby wyświetlić "Scene"
      setWaterBlocksArray(waterBlocks);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div style={{ height: "1vh", display: "flex", flexDirection: "column", alignItems: "center" }}>
      <div className="center" style={{paddingTop:'18vh', position: "relative", zIndex: 2, width: "100%", height: "800px", top: "40px", justifyContent: "center", alignItems: "center" }}>
        <img className="icon" src={whitecloud} alt="Ikona" />
      </div>

      {showScene && (
      <div style={{ position: "relative", zIndex: 2, width: "100%", height: "800px", top: "40px", justifyContent: "center", alignItems: "center" }}>
        <Scene key={key} surface={surfaceArray} waterBlocks={waterBlocks} />
      </div>
      )}

      <div className="center" style={{paddingTop:'1vh'}}>    
        <div style={{ paddingTop: '200px', backgroundColor: '#2196F3', color: 'white', borderRadius: '10px', padding: '10px', textAlign: 'center', width: '50%' }}>
          <div style={{ marginBottom: '10px' }}>    
            <input type="text" ref={inputRef} style={{ padding: '10px', borderRadius: '5px', border: 'none', boxShadow: '0 2px 4px rgba(0,0,0,.2)' }} />
          </div>
          <div>
            <button onClick={handleClick} style={{ backgroundColor: 'white', color: '#2196F3', padding: '10px', borderRadius: '5px', border: 'none', boxShadow: '0 2px 4px rgba(0,0,0,.2)', cursor: 'pointer' }}>Sprawdź opady</button>
          </div>
        </div>
      </div>
    </div>
  );
}
export default App;
