import * as THREE from "three";
import React, { useRef, useState } from "react";
import { Canvas, useFrame } from "@react-three/fiber";

type Props = {
  surface: number[];
};

const Box = ({ x, y, z, color }: { x: number; y: number; z: number; color: string }) => {
  const meshRef = useRef<THREE.Mesh>(null);

  const [hovered, setHover] = useState(false);
  const [active, setActive] = useState(false);

  useFrame(({ clock }) => {
    const mesh = meshRef.current!;
    mesh.position.y = Math.sin(clock.getElapsedTime() + x) * 0.3 + y;
  });

  return (
    <mesh
      ref={meshRef}
      position={[x, y, z]}
      scale={active ? [1.5, 1.5, 1.5] : [1, 1, 1]}
      onClick={() => setActive(!active)}
      onPointerOver={() => setHover(true)}
      onPointerOut={() => setHover(false)}
    >
      <boxBufferGeometry args={[1, 1, 1]} />
      <meshStandardMaterial color={hovered ? "hotpink" : color} />
    </mesh>
  );
};

const Surface = ({ surface }: Props) => {
  const cubes: JSX.Element[] = [];

  let x = 0;
  let z = 0;

  surface.forEach((y, i) => {
    for (let j = 0; j < y; j++) {
      cubes.push(<Box key={`${i}-${j}`} x={x} y={y / 2} z={z} color="#bada55" />);
      z += 1;
    }

    x += 1;
    z = 0;
  });

  return <>{cubes}</>;
};

const Scene = ({ surface }: Props) => {
  return (
    <Canvas style={{ width: "100%", height: "100%" }}>
      <ambientLight intensity={0.2} />
      <pointLight position={[10, 10, 10]} />
      <Surface surface={surface} />
    </Canvas>
  );
};

export default Scene;
