import React, { useState, useRef } from 'react';
import lottie from 'lottie-web';
import animationData from './animation/cloud.json';
import { Stage, Layer, Rect, Group } from "react-konva";
import whitecloud from './assets/whitecloud.png';
import { getWaterVolume } from './api/controller';
import findWaterBlocks from './utils/findWaterBlocks';
import './App.css';
import Scene from "./components/scene";

function App() {
  const inputRef = useRef<HTMLInputElement>(null);
  const [surfaceArray, setSurfaceArray] = useState<number[]>([]);
  const [waterBlocks, setWaterBlocksArray] = useState<number[][]>([]);
  const [key, setKey] = useState(0);
  const [showScene, setShowScene] = useState(false);

  const handleClick = async () => {
    setKey(key + 1); // zmiana klucza, aby wymusić ponowne wyrenderowanie
    setShowScene(true); // ustawienie flagi, aby wyświetlić "Scene"
    try {
      const inputValue = inputRef.current?.value;
      if (!inputValue) {
        console.log('Pole tekstowe jest puste');
        return;
      }
      const surfaceArray = inputValue.split(',').map(Number);
      console.log(surfaceArray);
      setSurfaceArray(surfaceArray);
      const waterBlocks = findWaterBlocks(surfaceArray);
      const volume = await getWaterVolume(surfaceArray);
      console.log(volume)
      setKey(key + 1); // zmiana klucza, aby wymusić ponowne wyrenderowanie
      setShowScene(true); // ustawienie flagi, aby wyświetlić "Scene"
      setWaterBlocksArray(waterBlocks);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div style={{ height: "80vh", display: "flex", flexDirection: "column", alignItems: "center" }}>
      <div className="center">
        <img className="icon" src={whitecloud} alt="Ikona" />
        <div style={{ position: "relative", zIndex: 1, textAlign: 'center' }}>
          <input type="text" ref={inputRef} />
          <br />
          <button onClick={handleClick}>Sprawdź opady</button>
        </div>
      </div>
      {showScene && (
      <div style={{ position: "relative", zIndex: 2, width: "50%", height: "600%", top: "20px", display: "flex", justifyContent: "center", alignItems: "center" }}>
        <Scene key={key} surface={surfaceArray} waterBlocks={waterBlocks} />
      </div>
      )}
    </div>
  );
}

export default App;
