import * as THREE from "three";
import React, { useRef } from "react";
import { Canvas } from "@react-three/fiber";

type Props = {
  surface: number[];
};

const boxArgs = [2, 1, 1];
const edgesGeometry = new THREE.EdgesGeometry(new THREE.BoxGeometry(...boxArgs));

const Box = ({ x, y, z }: { x: number; y: number; z: number }) => {
  return (
    <mesh position={[x, y, z]}>
      <boxBufferGeometry args={boxArgs} />
      <meshStandardMaterial color="gray" />
      <lineSegments geometry={edgesGeometry}>
        <lineBasicMaterial color="white" linewidth={2} />
      </lineSegments>
    </mesh>
  );
};

const Surface = ({ surface }: Props) => {
  const cubes: JSX.Element[] = [];
  let x = 0;
  let y = 0;
  let z = 0;

  surface.forEach((height, i) => {
    for (let j = 0; j < height; j++) {
      cubes.push(<Box key={`${i}-${j}`} x={x} y={y} z={z} />);
      y += boxArgs[1];
    }

    y = 0;
    x += boxArgs[0];
  });

  return <>{cubes}</>;
};

const Scene = ({ surface }: Props) => {
    const numElements = surface.reduce((acc, val) => acc + val, 0);
    const surfaceWidth = numElements * boxArgs[0];
    const surfaceHeight = surface.length * boxArgs[1];
  
    const centerX = surfaceWidth / 2;
    const centerY = -surfaceHeight / 2;
  
    const cameraDistance = Math.max(surfaceWidth, surfaceHeight) + numElements * boxArgs[2] + 10;
  

    console.log(centerX);
    console.log(centerY)
    return (
      <Canvas
        style={{ width: "100%", height: "100%" }}
        camera={{ position: [centerX, centerY, cameraDistance], zoom: 1 }}
      >
        <ambientLight intensity={0.2} />
        <pointLight position={[10, 10, 10]} />
        <Surface surface={surface} />
      </Canvas>
    );
  };

export default Scene;
