import * as THREE from "three";
import React, { useRef, useState } from "react";
import { Canvas, useFrame } from "@react-three/fiber";


// TYPY
type Props = {
  surface: number[];
};

const boxArgs = [2, 1, 1];
const edgesGeometry = new THREE.EdgesGeometry(new THREE.BoxGeometry(...boxArgs));

const Box = ({ x, y, z }: { x: number; y: number; z: number }) => {
  return (
    <mesh position={[x, y, z]}>
      <boxBufferGeometry args={boxArgs} />
      <meshStandardMaterial color="gray" />
      <lineSegments geometry={edgesGeometry}>
        <lineBasicMaterial color="white" linewidth={2} />
      </lineSegments>
    </mesh>
  );
};

const Surface = ({ surface }: Props) => {
  const cubes: JSX.Element[] = [];
  let x = 0;
  let y = 0;
  let z = 0;

  let middleColumnIndex = Math.floor(surface.length / 2); // Indeks środkowej kolumny

  surface.forEach((height, i) => {
    const rowWidth = height * boxArgs[0]; // Szerokość wiersza
    console.log("rowWidth")
    console.log(rowWidth)
    const rowStartX = -rowWidth / 2 + boxArgs[0] / 2; // Początkowa pozycja X wiersza
    for (let j = 0; j < height; j++) {
      const posX = rowStartX + j * boxArgs[0]; // Obliczenie pozycji X kostki
      cubes.push(<Box key={`${i}-${j}`} x={posX} y={y} z={z} />);
      console.log(x, y, z);
      y += boxArgs[1];
    }

    y = 0;
    x += rowWidth;
  });

  const numElements = cubes.length;
  const surfaceWidth = surface.reduce((acc, val) => acc + val, 0) * boxArgs[0];
  const surfaceHeight = surface.length * boxArgs[1];

  let cameraDistance = Math.max(surfaceWidth, surfaceHeight) + numElements * boxArgs[2];


  return {
    elements: (
      <>
        {cubes}
      </>
    ),
    width: surfaceWidth,
    height: surfaceHeight,
    distance: cameraDistance,
  };
};


const Axes = () => {
  const axesHelperRef = useRef<THREE.AxesHelper>(null);

  useFrame((state) => {
    if (axesHelperRef.current) {
      axesHelperRef.current.scale.set(50, 50, 50);
    }
  });

  return <axesHelper ref={axesHelperRef} args={[5]} />;
};

const Scene = ({ surface }: Props) => {
  const { elements } = Surface({ surface });
  return (
    <Canvas style={{ background: "pink" }}>
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      console.log(elements)
      <group>
        {elements}
      </group>
      <Axes />
    </Canvas>
  );
};

export default Scene;
