import whitecloud from './assets/whitecloud.png';
import Scene from "./components/scene";
import { handleClick, handleReturn } from "./utils/handleClick";
import './App.css';
import { useState, useRef } from 'react';
import { GridLoader } from 'react-spinners';

function App() {
  const inputRef = useRef<HTMLInputElement>(null);
  const [surfaceArray, setSurfaceArray] = useState<number[]>([]);
  const [waterBlocks, setWaterBlocksArray] = useState<number[][]>([])
  const [showScene, setShowScene] = useState(true);
  const [key, setKey] = useState(0);
  const [showVolume, setShowVolume] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [showVolumeValue, setShowVolumeValue] = useState(false);
  const [volume, setVolume] = useState<number | null>(null);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);

  const handleClickWrapper = async () => {
    await handleClick({
      inputRef,
      setShowScene,
      setSurfaceArray,
      setWaterBlocksArray,
      setShowVolume,
      setShowVolumeValue,
      setIsLoading,
      setVolume,
      setErrorMessage,
    });
  };
  /*
  const handleReturnWrapper = async () => {
    await handleReturn(setShowVolume, setSurfaceArray, setShowScene, setVolume, setIsLoading);
  };
*/

  return (
    <div className="cloudContainer">
      <div className="center" style={{paddingTop:'18vh'}}>
        <img className="whiteCloud" src={whitecloud} alt="whiteCloud" />
      </div>
        {showScene && (
          <div className="sceneContainer">
            <Scene key={key} surface={surfaceArray} waterBlocks={waterBlocks} />
          </div>
        )}
      <div className="inputBox">
      {!showVolume && (
        <div>
          <h4> Enter array to check rain volume </h4>
          <h6> Data format example: 3, 2, 3</h6>
          <input className="inputText" type="text" ref={inputRef} />
          <div>
            <button className="button" onClick={handleClickWrapper}>Check volume</button>
          </div>  
        </div>
      )}
      {showVolume && (
        <div>
          <h1>VOLUME</h1>    
          {isLoading ? (
            <GridLoader size={15} color={"#249eac"} loading={isLoading} style={{paddingBottom:'1vh'}}/>
            ): 
            <div>
              <h1>{volume}</h1>
            </div>    
          }
          <div>
            <button className="button2" onClick={handleReturn}>Enter new value</button>
          </div> 
        </div>
      )}
      </div>
    </div>
  );
}
export default App;
