import * as THREE from "three";
import React, { useRef } from "react";
import { Canvas } from "@react-three/fiber";

type Props = {
  surface: number[];
};

const Box = ({ x, y, z }: { x: number; y: number; z: number }) => {
  const edges = useRef<THREE.LineSegments>(null);

  return (
    <mesh position={[x, y, z]}>
      <boxBufferGeometry args={[1, 1, 1]} />
      <meshStandardMaterial color="gray" />
      <mesh>
        <lineBasicMaterial color="white" linewidth={2} />
      </mesh>
    </mesh>
  );
};

const Surface = ({ surface }: Props) => {
  const cubes: JSX.Element[] = [];
  let x = 0;
  let z = 0;

  surface.forEach((y, i) => {
    for (let j = 0; j < y; j++) {
      cubes.push(<Box key={`${i}-${j}`} x={x} y={y / 2} z={z} />);
      z += 1;
    }

    x += 1;
    z = 0;
  });

  return <>{cubes}</>;
};

const Scene = ({ surface }: Props) => {
  return (
    <Canvas style={{ width: "100%", height: "100%" }} camera={{ position: [0, -10, 0], scale: [100, 100, 100] }}>
      <ambientLight intensity={0.2} />
      <pointLight position={[10, 10, 10]} />
      <Surface surface={surface} />
    </Canvas>
  );
};

export default Scene;
