import * as THREE from "three";
import React, { useRef, useState } from "react";
import { Canvas, useFrame } from "@react-three/fiber";

type Position = [number, number, number];

const CubePair = () => {
  const [position, setPosition] = useState<Position>([0, 0, 0]);

  const handleMouseMove = (event: React.MouseEvent<HTMLCanvasElement, MouseEvent>) => {
    setPosition([event.clientX, event.clientY, 0]);
  };

  return (
    <>
      <mesh position={[-1, 0, 0]}>
        <boxBufferGeometry args={[1, 1, 1]} />
        <meshStandardMaterial color="red" />
      </mesh>
      <mesh position={[1, 0, 0]}>
        <boxBufferGeometry args={[1, 1, 1]} />
        <meshStandardMaterial color="blue" />
      </mesh>
    </>
  );
};

const Axes = () => {
  const axesHelperRef = useRef<THREE.AxesHelper>(null);

  useFrame((state) => {
    if (axesHelperRef.current) {
      axesHelperRef.current.scale.set(50, 50, 50);
    }
  });

  return <axesHelper ref={axesHelperRef} args={[5]} />;
};

const Scene = () => {
  return (
    <Canvas style={{ background: "white" }}>
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <CubePair />
      <CubePair />
      <Axes />
    </Canvas>
  );
};

export default Scene;
