import * as THREE from "three";
import React, { useRef, useState } from "react";
import { Canvas, useFrame } from "@react-three/fiber";

// TYPY
type Props = {
  surface: number[];
};
const boxArgs = [2, 1, 1];
const edgesGeometry = new THREE.EdgesGeometry(new THREE.BoxGeometry(...boxArgs));
const Box = ({ x, y, z }: { x: number; y: number; z: number }) => {
  return (
    <mesh position={[x, y, z]}>
      <boxBufferGeometry args={boxArgs} />
      <meshStandardMaterial color="gray" />
      <lineSegments geometry={edgesGeometry}>
        <lineBasicMaterial color="white" linewidth={2} />
      </lineSegments>
    </mesh>
  );
};
const Surface = ({ surface }: Props) => {
  const cubes: JSX.Element[] = [];
  const water: JSX.Element[] = [];

  let x = 0;
  let y = 0;
  const surfaceLength = surface.length;

  //SKALOWANIE X [-polowa/mask do polowa/maks] na osi
  const rowWidth = surfaceLength * boxArgs[0]; // Szerokość wiersza
  let rowStartX = -rowWidth + boxArgs[0]; // Początkowa pozycja X wiersza
  rowStartX = rowStartX / 2;

  //SKALOWANIE Y [maksymalna wysokość] na osi
  const maxSurfaceValue = Math.max(...surface);
  let columnHeight = -(maxSurfaceValue - 1) * boxArgs[1];
  columnHeight = columnHeight / 2;

  const exportRowStartX = rowStartX;
  const exportColumnHeight = columnHeight;

  const distance = -(Math.sqrt(rowStartX ** 2 + columnHeight ** 2) + 5);
  let z = distance;

  // Tworzenie klocków powierzchni
  surface.forEach((surfaceIndex, i) => {
    for (let j = 0; j < surfaceIndex; j++) {
      const posX = rowStartX;
      const posY = columnHeight + j * boxArgs[1]; // Obliczenie pozycji Y kostki
      cubes.push(<Box key={`${i}-${j}`} x={posX} y={posY} z={z} />);
    }
    rowStartX += boxArgs[0];
  });

  // Sprawdzenie, gdzie znajdują się miejsca dla wody
  for (let i = 0; i < surfaceLength - 1; i++) {
    const leftHeight = surface[i];
    const rightHeight = surface[i + 1];
    const maxHeight = Math.max(leftHeight, rightHeight);

    for (let j = 1; j < maxHeight; j++) {
      const leftHeightDiff = maxHeight - leftHeight;
      const rightHeightDiff = maxHeight - rightHeight;

      // Sprawdzenie, czy miejsce jest odpowiednie dla wody
      if (j <= leftHeightDiff && j <= rightHeightDiff) {
        const posX = exportRowStartX + i * boxArgs[0];
        const posY = exportColumnHeight + j * boxArgs[1];
        water.push(<Box key={`water-${i}-${j}`} x={posX} y={posY} z={z} />);
      }
    }
  }

  return {
    exportRowStartX,
    exportColumnHeight,
    elements: (
      <>
        {cubes}
        {water}
      </>
    ),
  };
};

const Axes = () => {
  const axesHelperRef = useRef<THREE.AxesHelper>(null);
  useFrame((state) => {
    if (axesHelperRef.current) {
      axesHelperRef.current.scale.set(50, 50, 50);
    }
  });
  return <axesHelper ref={axesHelperRef} args={[5]} />;
};


const Scene = ({ surface }: Props) => {
  const { elements, exportRowStartX, exportColumnHeight } = Surface({ surface });

  // Obliczanie pozycji wody
  const waterPositions = [];
  const maxSurfaceValue = Math.max(...surface);
  const waterLevel = maxSurfaceValue - 1;
  for (let i = 0; i < surface.length - 1; i++) {
    const heightDiff = surface[i] - surface[i + 1];
    if (heightDiff <= 0) {
      continue;
    }
    const waterSurfaceLevel = waterLevel - surface[i + 1];
    if (waterSurfaceLevel >= heightDiff) {
      for (let j = 0; j < heightDiff; j++) {
        const posX = exportRowStartX + i * boxArgs[0];
        const posY = exportColumnHeight - (surface[i + 1] + j) * boxArgs[1];
        waterPositions.push([posX, posY]);
      }
    } else {
      const posX = exportRowStartX + i * boxArgs[0];
      const posY = exportColumnHeight - (waterLevel - heightDiff) * boxArgs[1];
      waterPositions.push([posX, posY]);
    }
  }


  return (
    <Canvas>
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <group scale={[1, 1, 1]}>
        {elements}
        {waterPositions.map(([x, y], i) => (
          <mesh position={[x, y, -8]} key={`water-${i}`}>
            <boxBufferGeometry args={boxArgs} />
            <meshStandardMaterial color="blue" />
          </mesh>
        ))}
      </group>
    </Canvas>
  );
};

export default Scene;
