import React, { useState } from 'react';
import lottie from 'lottie-web';
import animationData from './animation/cloud.json';
import whitecloud from './assets/whitecloud.png';
import './App.css';

function App() {
  const [inputValue, setInputValue] = useState('');
  const [surfaceArray, setSurfaceArray] = useState<number[]>([]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(e.target.value);
  };

  const handleClick = () => {
    const newSurfaceArray = inputValue.split(',').map((value) => parseInt(value.trim(), 10));
    setSurfaceArray(newSurfaceArray);
  };

  return (
    <div className="center">
      <img className="icon" src={whitecloud} alt="Ikona" />
      <div style={{ display: "flex", flexDirection: "column", alignItems: "center", marginTop: "1rem" }}>
        <input type="text" value={inputValue} onChange={handleInputChange} />
        <br />
        <button onClick={handleClick}>Sprawd≈∫ opady</button>
      </div>
    </div>
  );
}

export default App;
