import * as THREE from "three";
import React, { useRef } from "react";
import { Canvas } from "@react-three/fiber";

type Props = {
  surface: number[];
};

const boxArgs = [2, 1, 1];
const edgesGeometry = new THREE.EdgesGeometry(new THREE.BoxGeometry(...boxArgs));

const Box = ({ x, y, z }: { x: number; y: number; z: number }) => {
  return (
    <mesh position={[x, y, z]}>
      <boxBufferGeometry args={boxArgs} />
      <meshStandardMaterial color="gray" />
      <lineSegments geometry={edgesGeometry}>
        <lineBasicMaterial color="white" linewidth={2} />
      </lineSegments>
    </mesh>
  );
};

const Surface = ({ surface }: Props) => {
  const cubes: JSX.Element[] = [];
  let x = 0;
  let y = 0;
  let z = 0;

  let middleColumnIndex = Math.floor(surface.length / 2); // Indeks środkowej kolumny
  let centerX = 0; // Zdefiniowanie centerX

  surface.forEach((height, i) => {
    for (let j = 0; j < height; j++) {
      cubes.push(<Box key={`${i}-${j}`} x={x} y={y} z={z} />);
      y += boxArgs[1];

      if (i === middleColumnIndex && j === Math.floor(height / 2)) {
        centerX = x; // Ustawienie pozycji x środkowego sześcianu
      }
    }

    y = 0;
    x += boxArgs[0];
  });

  const numElements = cubes.length;
  const surfaceWidth = numElements * boxArgs[0];
  const surfaceHeight = surface.length * boxArgs[1];

  let centerY = -surfaceHeight / 2;
  let cameraDistance = Math.max(surfaceWidth, surfaceHeight) + numElements * boxArgs[2];

  return {
    centerX,
    elements: (
      <>
        {cubes}
      </>
    ),
    width: surfaceWidth,
    height: surfaceHeight,
    distance: cameraDistance,
  };
};

const Scene = ({ surface }: Props) => {
    const { centerX, elements, width, height } = Surface({ surface });
  
    // wybieramy większą wartość z szerokości i wysokości
    const maxSurfaceDimension = Math.max(width, height);
  
    const cameraDistance = maxSurfaceDimension + 10; // dodajemy odległość między kamerą a najdalszym elementem w scenie (tutaj ustawiłem arbitralnie na 10)
  
    return (
      <div>
<Canvas
  style={{ width: "100%", height: "100%" }}
  camera={{
    position: [centerX, -height / 2, cameraDistance],
    near: 0.1,
    far: 1000,
  }}
  onCreated={({ camera }) => {
    const centerX = width / 2; // obliczanie środka powierzchni
    camera.lookAt(0, 0, 0); // ustawienie kamery, aby patrzyła na środek powierzchni
  }}
>
  <ambientLight intensity={0.5} />
  <pointLight position={[10, 10, 10]} />
  <group scale={[width/2, 1, 1]} position={[-20, -height / 2, 0]}>
    {elements}
  </group>
</Canvas>
      </div>
    );
  };
  

export default Scene;
